import {useState} from "react";
import EndScreen from "./end-screen";
import Stats from "./stats";
import TriviaItem from "./trvia-item";
import triviaData from "./trivia-data";

/*
The game is responsible for orchestrating
the flow of the quiz game.
*/
function Game() {
  const [gameState, SetGameState] = useState({
    score: 0,
    triviaIndex: 0,
    isGameOver: false,
  });

  const {score, triviaIndex, isGameOver} = gameState;
  const triviaQuestion = triviaData[triviaIndex];
  const {correct_answer, incorrect_answers, question} = triviaQuestion;

  return (
    <>
    <p>{score}</p>
    <p>{triviaIndex}</p>
    <p>{isGameOver ? "Game is over" : "Game is not over"}</p>
    <p>{question}</p>
      <Stats />
      <TriviaItem />
      <EndScreen />
    </>
  );
}

export default Game;
