/*
The stats compnent renders the score 
adn current question number.
*/
function Stat({label, value}) {
  return (
    <ul className="stats__stat-constainer">
      <li>
        <div className="stats__stat-label">{label}</div>
        <div className="stats__stat-value">{value}</div>
      </li>
    </ul>
  );
}

function Stats({ score, QuestionNumber, totalQuestions }) {
  return (
    <ul className="stats__stat-constainer">
      <Stat label="Score" value={score}/>
      <Stat label="Question Numnber" value={`${QuestionNumber} / ${totalQuestions}`}/>

    </ul>
  );
}

export default Stats;
