import { useState } from "react";
function ClickButton() {
    //Array destructuring demo;
    //const animals = ["cat", "dog", "cow"];
    //const[animal1, animal2, animal3] =animals;
    //console.log(animal2);
    //console.log(animal1);


// useState returns back an array that looks like: [currentvalue, setterfunction]
    const [numClicks, setNumClicks] = useState(0);

    const onButtonClick = () => {

        setNumClicks(numClicks + 1);
    };
    return <button onClick={onButtonClick}>you clicked {numClicks}x times</button>;
}

export default ClickButton;
