import ChatMessage from "./demos/chat-message";
import SpeakButton from "./demos/speak-button";
import WelcomeMessage from "./demos/welcome-message";
//components can take inputs in the form of props (or properties).
//pascal case
//a (functional) component is just a js function that returns jsx
function App() {
  //inside of jsx, it allows us to insert a js expression
  return (
    <main>
      <h1>Henloooo</h1>
      <p>My first react app!</p>
      <h2>Welcome</h2>
      {/*name and greeting become properties on an object passed in to the Welcome message*/}
      <WelcomeMessage name="jo" greeting="Hello buddy!" />
      <WelcomeMessage name="jose" greeting="sup bro" />
      <WelcomeMessage name="person" greeting="Welcome" />
      <h2>Buttons</h2>
      <SpeakButton message="Hello there" rate={1} pitch={1} />
      <SpeakButton message="Speeeeen" rate={5} pitch={2} />
      <h2>Alert buttons</h2>
      
      <h2>Chat</h2>
      <ChatMessage
        message="Hey!, how the coding going bud"
        userName="polaroids"
        date="02/15/21"
      />
      <ChatMessage
        message="it's going well, learning at least"
        userName="pottedplant"
        date="02/15/21"
      />
      <ChatMessage
        message="Hopefully you'll get a hang of it :)"
        userName="polaroids"
        date="02/15/21"
      />

      <h2>Social Media</h2>
    </main>
  );
}

export default App;
