import EndScreen from "./end-screen";
import Stats from "./stats";
import TriviaItem from "./trvia-item";

/*
The game is responsible for orchestrating
the flow of the quiz game.
*/
function Game() {
  const [value, SetValuue] = useState({
    score: 0,
    triviaIndex: 0,
    isGameOver: false,
  });
  return (
    <>
      <Stats />
      <TriviaItem />
      <EndScreen />
    </>
  );
}

export default Game;
